import React, { useEffect } from 'react'; // Importez useEffect
import SectionTitle from '../Miscellaneous/SectionTitle';

const SkillsSection = ({ skillsData }) => { // Renommez le paramètre en skillsData

  useEffect(() => { // Utilisez useEffect pour le code qui manipule le DOM
      const skillBars = document.querySelectorAll('.progress-bar-line');

    skillBars.forEach(skillBar => {
        const width = skillBar.getAttribute('aria-valuenow');
        skillBar.style.width = width + '%';
       // console.log(width);
      const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('in-view');
          }
        });
      });
      observer.observe(skillBar);

    });
  }, []); // Le tableau de dépendances vide [] garantit que cela ne s'exécute qu'une fois, au montage

  if (!skillsData || skillsData.length === 0) {
    return <p>Aucune donnée de compétence disponible.</p>; // Gérer le cas où skillsData est vide
  }

  return (
    <section id="skills" className="skills-area pt-2 pt-lg-2 pb-1">
      <div className="container">
        <div className="row align-items-center">
          <div className="col-12 col-md-4">
            <SectionTitle
              title={skillsData.title} // Utilisez skillsData.title
              subTitle={skillsData.subTitle} // Utilisez skillsData.subTitle
            />
          </div>
          <div className="col-12 col-md-8 mt-4 mt-md-0">
            <div className="skills-items">
                {skillsData.skills && skillsData.skills.length > 0 ? (
                    skillsData.skills.map((skill, index) => (
                        <div className="skill-item" key={index}>
                        <h4 className="progress-title">{skill.label}</h4>
                        <div className="progress progress-bar-line">
                            <div
                            className="progress-bar"
                            role="progressbar"
                            aria-valuenow={skill.percentage}
                            aria-valuemin="0"
                            aria-valuemax="100"
                            >
                            <span>{skill.percentage}</span>
                            </div>
                        </div>
                        </div>
                    ))
                ):(
                    <p>Aucune compétence à afficher pour cette section</p>
                )}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default SkillsSection;
